package com.tadpole.poem.web.rest;

import com.tadpole.poem.PoemdataApp;
import com.tadpole.poem.domain.Poem;
import com.tadpole.poem.repository.PoemRepository;
import com.tadpole.poem.service.PoemService;

import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;
import static org.hamcrest.Matchers.hasItem;
import org.mockito.MockitoAnnotations;
import org.springframework.boot.test.IntegrationTest;
import org.springframework.boot.test.SpringApplicationConfiguration;
import org.springframework.http.MediaType;
import org.springframework.http.converter.json.MappingJackson2HttpMessageConverter;
import org.springframework.data.web.PageableHandlerMethodArgumentResolver;
import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;
import org.springframework.test.context.web.WebAppConfiguration;
import org.springframework.test.util.ReflectionTestUtils;
import org.springframework.test.web.servlet.MockMvc;
import org.springframework.test.web.servlet.setup.MockMvcBuilders;
import org.springframework.transaction.annotation.Transactional;

import javax.annotation.PostConstruct;
import javax.inject.Inject;
import java.util.List;

import static org.assertj.core.api.Assertions.assertThat;
import static org.springframework.test.web.servlet.request.MockMvcRequestBuilders.*;
import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.*;


/**
 * Test class for the PoemResource REST controller.
 *
 * @see PoemResource
 */
@RunWith(SpringJUnit4ClassRunner.class)
@SpringApplicationConfiguration(classes = PoemdataApp.class)
@WebAppConfiguration
@IntegrationTest
public class PoemResourceIntTest {

    private static final String DEFAULT_ANTHOR_NAME = "AAAAA";
    private static final String UPDATED_ANTHOR_NAME = "BBBBB";
    private static final String DEFAULT_TITLE = "AAAAA";
    private static final String UPDATED_TITLE = "BBBBB";
    private static final String DEFAULT_CONTENT = "";
    private static final String UPDATED_CONTENT = "";
    private static final String DEFAULT_YEAR = "AAAAA";
    private static final String UPDATED_YEAR = "BBBBB";
    private static final String DEFAULT_PERIOD = "AAAAA";
    private static final String UPDATED_PERIOD = "BBBBB";
    private static final String DEFAULT_TAG = "AAAAA";
    private static final String UPDATED_TAG = "BBBBB";
    private static final String DEFAULT_RESOURCE_ID = "AAAAA";
    private static final String UPDATED_RESOURCE_ID = "BBBBB";
    private static final String DEFAULT_TITLE_PINYIN = "AAAAA";
    private static final String UPDATED_TITLE_PINYIN = "BBBBB";

    @Inject
    private PoemRepository poemRepository;

    @Inject
    private PoemService poemService;

    @Inject
    private MappingJackson2HttpMessageConverter jacksonMessageConverter;

    @Inject
    private PageableHandlerMethodArgumentResolver pageableArgumentResolver;

    private MockMvc restPoemMockMvc;

    private Poem poem;

    @PostConstruct
    public void setup() {
        MockitoAnnotations.initMocks(this);
        PoemResource poemResource = new PoemResource();
        ReflectionTestUtils.setField(poemResource, "poemService", poemService);
        this.restPoemMockMvc = MockMvcBuilders.standaloneSetup(poemResource)
            .setCustomArgumentResolvers(pageableArgumentResolver)
            .setMessageConverters(jacksonMessageConverter).build();
    }

    @Before
    public void initTest() {
        poem = new Poem();
        poem.setAnthorName(DEFAULT_ANTHOR_NAME);
        poem.setTitle(DEFAULT_TITLE);
        poem.setContent(DEFAULT_CONTENT);
        poem.setYear(DEFAULT_YEAR);
        poem.setPeriod(DEFAULT_PERIOD);
        poem.setTag(DEFAULT_TAG);
        poem.setResourceId(DEFAULT_RESOURCE_ID);
        poem.setTitlePinyin(DEFAULT_TITLE_PINYIN);
    }

    @Test
    @Transactional
    public void createPoem() throws Exception {
        int databaseSizeBeforeCreate = poemRepository.findAll().size();

        // Create the Poem

        restPoemMockMvc.perform(post("/api/poems")
                .contentType(TestUtil.APPLICATION_JSON_UTF8)
                .content(TestUtil.convertObjectToJsonBytes(poem)))
                .andExpect(status().isCreated());

        // Validate the Poem in the database
        List<Poem> poems = poemRepository.findAll();
        assertThat(poems).hasSize(databaseSizeBeforeCreate + 1);
        Poem testPoem = poems.get(poems.size() - 1);
        assertThat(testPoem.getAnthorName()).isEqualTo(DEFAULT_ANTHOR_NAME);
        assertThat(testPoem.getTitle()).isEqualTo(DEFAULT_TITLE);
        assertThat(testPoem.getContent()).isEqualTo(DEFAULT_CONTENT);
        assertThat(testPoem.getYear()).isEqualTo(DEFAULT_YEAR);
        assertThat(testPoem.getPeriod()).isEqualTo(DEFAULT_PERIOD);
        assertThat(testPoem.getTag()).isEqualTo(DEFAULT_TAG);
        assertThat(testPoem.getResourceId()).isEqualTo(DEFAULT_RESOURCE_ID);
        assertThat(testPoem.getTitlePinyin()).isEqualTo(DEFAULT_TITLE_PINYIN);
    }

    @Test
    @Transactional
    public void getAllPoems() throws Exception {
        // Initialize the database
        poemRepository.saveAndFlush(poem);

        // Get all the poems
        restPoemMockMvc.perform(get("/api/poems?sort=id,desc"))
                .andExpect(status().isOk())
                .andExpect(content().contentType(MediaType.APPLICATION_JSON))
                .andExpect(jsonPath("$.[*].id").value(hasItem(poem.getId().intValue())))
                .andExpect(jsonPath("$.[*].anthorName").value(hasItem(DEFAULT_ANTHOR_NAME.toString())))
                .andExpect(jsonPath("$.[*].title").value(hasItem(DEFAULT_TITLE.toString())))
                .andExpect(jsonPath("$.[*].content").value(hasItem(DEFAULT_CONTENT.toString())))
                .andExpect(jsonPath("$.[*].year").value(hasItem(DEFAULT_YEAR.toString())))
                .andExpect(jsonPath("$.[*].period").value(hasItem(DEFAULT_PERIOD.toString())))
                .andExpect(jsonPath("$.[*].tag").value(hasItem(DEFAULT_TAG.toString())))
                .andExpect(jsonPath("$.[*].resourceId").value(hasItem(DEFAULT_RESOURCE_ID.toString())))
                .andExpect(jsonPath("$.[*].titlePinyin").value(hasItem(DEFAULT_TITLE_PINYIN.toString())));
    }

    @Test
    @Transactional
    public void getPoem() throws Exception {
        // Initialize the database
        poemRepository.saveAndFlush(poem);

        // Get the poem
        restPoemMockMvc.perform(get("/api/poems/{id}", poem.getId()))
            .andExpect(status().isOk())
            .andExpect(content().contentType(MediaType.APPLICATION_JSON))
            .andExpect(jsonPath("$.id").value(poem.getId().intValue()))
            .andExpect(jsonPath("$.anthorName").value(DEFAULT_ANTHOR_NAME.toString()))
            .andExpect(jsonPath("$.title").value(DEFAULT_TITLE.toString()))
            .andExpect(jsonPath("$.content").value(DEFAULT_CONTENT.toString()))
            .andExpect(jsonPath("$.year").value(DEFAULT_YEAR.toString()))
            .andExpect(jsonPath("$.period").value(DEFAULT_PERIOD.toString()))
            .andExpect(jsonPath("$.tag").value(DEFAULT_TAG.toString()))
            .andExpect(jsonPath("$.resourceId").value(DEFAULT_RESOURCE_ID.toString()))
            .andExpect(jsonPath("$.titlePinyin").value(DEFAULT_TITLE_PINYIN.toString()));
    }

    @Test
    @Transactional
    public void getNonExistingPoem() throws Exception {
        // Get the poem
        restPoemMockMvc.perform(get("/api/poems/{id}", Long.MAX_VALUE))
                .andExpect(status().isNotFound());
    }

    @Test
    @Transactional
    public void updatePoem() throws Exception {
        // Initialize the database
        poemService.save(poem);

        int databaseSizeBeforeUpdate = poemRepository.findAll().size();

        // Update the poem
        Poem updatedPoem = new Poem();
        updatedPoem.setId(poem.getId());
        updatedPoem.setAnthorName(UPDATED_ANTHOR_NAME);
        updatedPoem.setTitle(UPDATED_TITLE);
        updatedPoem.setContent(UPDATED_CONTENT);
        updatedPoem.setYear(UPDATED_YEAR);
        updatedPoem.setPeriod(UPDATED_PERIOD);
        updatedPoem.setTag(UPDATED_TAG);
        updatedPoem.setResourceId(UPDATED_RESOURCE_ID);
        updatedPoem.setTitlePinyin(UPDATED_TITLE_PINYIN);

        restPoemMockMvc.perform(put("/api/poems")
                .contentType(TestUtil.APPLICATION_JSON_UTF8)
                .content(TestUtil.convertObjectToJsonBytes(updatedPoem)))
                .andExpect(status().isOk());

        // Validate the Poem in the database
        List<Poem> poems = poemRepository.findAll();
        assertThat(poems).hasSize(databaseSizeBeforeUpdate);
        Poem testPoem = poems.get(poems.size() - 1);
        assertThat(testPoem.getAnthorName()).isEqualTo(UPDATED_ANTHOR_NAME);
        assertThat(testPoem.getTitle()).isEqualTo(UPDATED_TITLE);
        assertThat(testPoem.getContent()).isEqualTo(UPDATED_CONTENT);
        assertThat(testPoem.getYear()).isEqualTo(UPDATED_YEAR);
        assertThat(testPoem.getPeriod()).isEqualTo(UPDATED_PERIOD);
        assertThat(testPoem.getTag()).isEqualTo(UPDATED_TAG);
        assertThat(testPoem.getResourceId()).isEqualTo(UPDATED_RESOURCE_ID);
        assertThat(testPoem.getTitlePinyin()).isEqualTo(UPDATED_TITLE_PINYIN);
    }

    @Test
    @Transactional
    public void deletePoem() throws Exception {
        // Initialize the database
        poemService.save(poem);

        int databaseSizeBeforeDelete = poemRepository.findAll().size();

        // Get the poem
        restPoemMockMvc.perform(delete("/api/poems/{id}", poem.getId())
                .accept(TestUtil.APPLICATION_JSON_UTF8))
                .andExpect(status().isOk());

        // Validate the database is empty
        List<Poem> poems = poemRepository.findAll();
        assertThat(poems).hasSize(databaseSizeBeforeDelete - 1);
    }
}
