package com.tadpole.poem.web.rest;

import com.tadpole.poem.PoemdataApp;
import com.tadpole.poem.domain.PoemTranslation;
import com.tadpole.poem.repository.PoemTranslationRepository;
import com.tadpole.poem.service.PoemTranslationService;

import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;
import static org.hamcrest.Matchers.hasItem;
import org.mockito.MockitoAnnotations;
import org.springframework.boot.test.IntegrationTest;
import org.springframework.boot.test.SpringApplicationConfiguration;
import org.springframework.http.MediaType;
import org.springframework.http.converter.json.MappingJackson2HttpMessageConverter;
import org.springframework.data.web.PageableHandlerMethodArgumentResolver;
import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;
import org.springframework.test.context.web.WebAppConfiguration;
import org.springframework.test.util.ReflectionTestUtils;
import org.springframework.test.web.servlet.MockMvc;
import org.springframework.test.web.servlet.setup.MockMvcBuilders;
import org.springframework.transaction.annotation.Transactional;

import javax.annotation.PostConstruct;
import javax.inject.Inject;
import java.util.List;

import static org.assertj.core.api.Assertions.assertThat;
import static org.springframework.test.web.servlet.request.MockMvcRequestBuilders.*;
import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.*;


/**
 * Test class for the PoemTranslationResource REST controller.
 *
 * @see PoemTranslationResource
 */
@RunWith(SpringJUnit4ClassRunner.class)
@SpringApplicationConfiguration(classes = PoemdataApp.class)
@WebAppConfiguration
@IntegrationTest
public class PoemTranslationResourceIntTest {

    private static final String DEFAULT_TRANSLATION = "";
    private static final String UPDATED_TRANSLATION = "";

    private static final Long DEFAULT_POEM_ID = 1L;
    private static final Long UPDATED_POEM_ID = 2L;

    @Inject
    private PoemTranslationRepository poemTranslationRepository;

    @Inject
    private PoemTranslationService poemTranslationService;

    @Inject
    private MappingJackson2HttpMessageConverter jacksonMessageConverter;

    @Inject
    private PageableHandlerMethodArgumentResolver pageableArgumentResolver;

    private MockMvc restPoemTranslationMockMvc;

    private PoemTranslation poemTranslation;

    @PostConstruct
    public void setup() {
        MockitoAnnotations.initMocks(this);
        PoemTranslationResource poemTranslationResource = new PoemTranslationResource();
        ReflectionTestUtils.setField(poemTranslationResource, "poemTranslationService", poemTranslationService);
        this.restPoemTranslationMockMvc = MockMvcBuilders.standaloneSetup(poemTranslationResource)
            .setCustomArgumentResolvers(pageableArgumentResolver)
            .setMessageConverters(jacksonMessageConverter).build();
    }

    @Before
    public void initTest() {
        poemTranslation = new PoemTranslation();
        poemTranslation.setTranslation(DEFAULT_TRANSLATION);
        poemTranslation.setPoemId(DEFAULT_POEM_ID);
    }

    @Test
    @Transactional
    public void createPoemTranslation() throws Exception {
        int databaseSizeBeforeCreate = poemTranslationRepository.findAll().size();

        // Create the PoemTranslation

        restPoemTranslationMockMvc.perform(post("/api/poem-translations")
                .contentType(TestUtil.APPLICATION_JSON_UTF8)
                .content(TestUtil.convertObjectToJsonBytes(poemTranslation)))
                .andExpect(status().isCreated());

        // Validate the PoemTranslation in the database
        List<PoemTranslation> poemTranslations = poemTranslationRepository.findAll();
        assertThat(poemTranslations).hasSize(databaseSizeBeforeCreate + 1);
        PoemTranslation testPoemTranslation = poemTranslations.get(poemTranslations.size() - 1);
        assertThat(testPoemTranslation.getTranslation()).isEqualTo(DEFAULT_TRANSLATION);
        assertThat(testPoemTranslation.getPoemId()).isEqualTo(DEFAULT_POEM_ID);
    }

    @Test
    @Transactional
    public void getAllPoemTranslations() throws Exception {
        // Initialize the database
        poemTranslationRepository.saveAndFlush(poemTranslation);

        // Get all the poemTranslations
        restPoemTranslationMockMvc.perform(get("/api/poem-translations?sort=id,desc"))
                .andExpect(status().isOk())
                .andExpect(content().contentType(MediaType.APPLICATION_JSON))
                .andExpect(jsonPath("$.[*].id").value(hasItem(poemTranslation.getId().intValue())))
                .andExpect(jsonPath("$.[*].translation").value(hasItem(DEFAULT_TRANSLATION.toString())))
                .andExpect(jsonPath("$.[*].poemId").value(hasItem(DEFAULT_POEM_ID.intValue())));
    }

    @Test
    @Transactional
    public void getPoemTranslation() throws Exception {
        // Initialize the database
        poemTranslationRepository.saveAndFlush(poemTranslation);

        // Get the poemTranslation
        restPoemTranslationMockMvc.perform(get("/api/poem-translations/{id}", poemTranslation.getId()))
            .andExpect(status().isOk())
            .andExpect(content().contentType(MediaType.APPLICATION_JSON))
            .andExpect(jsonPath("$.id").value(poemTranslation.getId().intValue()))
            .andExpect(jsonPath("$.translation").value(DEFAULT_TRANSLATION.toString()))
            .andExpect(jsonPath("$.poemId").value(DEFAULT_POEM_ID.intValue()));
    }

    @Test
    @Transactional
    public void getNonExistingPoemTranslation() throws Exception {
        // Get the poemTranslation
        restPoemTranslationMockMvc.perform(get("/api/poem-translations/{id}", Long.MAX_VALUE))
                .andExpect(status().isNotFound());
    }

    @Test
    @Transactional
    public void updatePoemTranslation() throws Exception {
        // Initialize the database
        poemTranslationService.save(poemTranslation);

        int databaseSizeBeforeUpdate = poemTranslationRepository.findAll().size();

        // Update the poemTranslation
        PoemTranslation updatedPoemTranslation = new PoemTranslation();
        updatedPoemTranslation.setId(poemTranslation.getId());
        updatedPoemTranslation.setTranslation(UPDATED_TRANSLATION);
        updatedPoemTranslation.setPoemId(UPDATED_POEM_ID);

        restPoemTranslationMockMvc.perform(put("/api/poem-translations")
                .contentType(TestUtil.APPLICATION_JSON_UTF8)
                .content(TestUtil.convertObjectToJsonBytes(updatedPoemTranslation)))
                .andExpect(status().isOk());

        // Validate the PoemTranslation in the database
        List<PoemTranslation> poemTranslations = poemTranslationRepository.findAll();
        assertThat(poemTranslations).hasSize(databaseSizeBeforeUpdate);
        PoemTranslation testPoemTranslation = poemTranslations.get(poemTranslations.size() - 1);
        assertThat(testPoemTranslation.getTranslation()).isEqualTo(UPDATED_TRANSLATION);
        assertThat(testPoemTranslation.getPoemId()).isEqualTo(UPDATED_POEM_ID);
    }

    @Test
    @Transactional
    public void deletePoemTranslation() throws Exception {
        // Initialize the database
        poemTranslationService.save(poemTranslation);

        int databaseSizeBeforeDelete = poemTranslationRepository.findAll().size();

        // Get the poemTranslation
        restPoemTranslationMockMvc.perform(delete("/api/poem-translations/{id}", poemTranslation.getId())
                .accept(TestUtil.APPLICATION_JSON_UTF8))
                .andExpect(status().isOk());

        // Validate the database is empty
        List<PoemTranslation> poemTranslations = poemTranslationRepository.findAll();
        assertThat(poemTranslations).hasSize(databaseSizeBeforeDelete - 1);
    }
}
